import React, { useState } from "react";
import styles from "../Styles/FilterOptions.module.css";

const FilterOptions = ({ temperamentOptions, onFilter, onSort }) => {
  const [filter, setFilter] = useState({
    name: "",
    temperament: "",
    minWeight: "",
    maxWeight: "",
    minHeight: "",
    maxHeight: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilter({ ...filter, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onFilter(filter);
  };

  const handleSort = (e) => {
    onSort(e.target.value);
  };

  return (

    <div className={styles.filterOptions}>
      <h2>Filter by:</h2>
      {temperaments && temperaments.map((temp) => (
        <button key={temp.id} onClick={() => onFilter(temp.name)}>
          {temp.name}
        </button>
      ))}
      <h2>Sort by:</h2>
      <button onClick={() => onSort("name")}>Name</button>
      <button onClick={() => onSort("weight")}>Weight</button>
      <form onSubmit={handleSubmit}>
        <label>
          Nombre:
          <input
            type="text"
            name="name"
            value={filter.name}
            onChange={handleChange}
          />
        </label>
        <label>
          Temperamento:
          <select name="temperament" value={filter.temperament} onChange={handleChange}>
            <option value="">Todos</option>
            {temperamentOptions.map((t) => (
              <option key={t.id} value={t.name}>
                {t.name}
              </option>
            ))}
          </select>
        </label>
        <label>
          Peso:
          <input
            type="number"
            name="minWeight"
            placeholder="Mínimo"
            value={filter.minWeight}
            onChange={handleChange}
          />
          <input
            type="number"
            name="maxWeight"
            placeholder="Máximo"
            value={filter.maxWeight}
            onChange={handleChange}
          />
        </label>
        <label>
          Altura:
          <input
            type="number"
            name="minHeight"
            placeholder="Mínimo"
            value={filter.minHeight}
            onChange={handleChange}
          />
          <input
            type="number"
            name="maxHeight"
            placeholder="Máximo"
            value={filter.maxHeight}
            onChange={handleChange}
          />
        </label>
        <button className={styles.filterButton}>Filtrar</button>
      </form>
      <div>
        <label>
          Ordenar por:
          <select onChange={handleSort}>
            <option value="DEFAULT">Por defecto</option>
            <option value="NAME_ASC">Nombre (A-Z)</option>
            <option value="NAME_DESC">Nombre (Z-A)</option>
            <option value="WEIGHT_ASC">Peso (menor a mayor)</option>
            <option value="WEIGHT_DESC">Peso (mayor a menor)</option>
          </select>
        </label>
      </div>
    </div>
  );
};

export default FilterOptions;

