import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
getDogs,
getDogById,
getTemperaments,
createNewDog,
} from "../redux/actions/actions";
import SearchBar from "./SearchBar";
import DogList from "./DogList";
import FilterOptions from "./FilterOptions";
import Pagination from "./Pagination";
import CreateDogForm from "./CreateDogForm";
import styles from "../Styles/HomePage.module.css";

const HomePage = () => {
const [showForm, setShowForm] = useState(false);
const [formSubmitted, setFormSubmitted] = useState(false);
const [searchTerm, setSearchTerm] = useState("");
const [filter, setFilter] = useState("");
const [sort, setSort] = useState("");

const dispatch = useDispatch();

useEffect(() => {
dispatch(getTemperaments());
dispatch(getDogs());
}, [dispatch]);

const { dogs, loading, error } = useSelector((state) => state.dogs);
const temperaments = useSelector((state) => state.temperaments);

const handleShowForm = () => {
setShowForm(!showForm);
};

const handleCloseForm = () => {
setShowForm(false);
};

useEffect(() => {
if (showForm) {
setFormSubmitted(false);
}
}, [showForm]);

const handleSubmitForm = (newDog) => {
dispatch(createNewDog(newDog));
setFormSubmitted(true);
};

const handleSearch = (searchTerm) => {
setSearchTerm(searchTerm);
};

const handleFilter = (filter) => {
setFilter(filter);
};

const handleSort = (sort) => {
setSort(sort);
};

const handleDogClick = (id) => {
dispatch(getDogById(id));
};

if (loading) {
return <div>Loading...</div>;
}

if (error) {
return <div>Error: {error.message}</div>;
}

return (
<div>
<h1 className={styles.title}>Razas de Perros</h1>
<div className={styles.buttonContainer}>
<button className={styles.button} onClick={handleShowForm}>
Agregar nueva raza
</button>
<SearchBar onSearch={handleSearch} />
<FilterOptions
       temperaments={temperaments}
       onFilter={handleFilter}
       onSort={handleSort}
     />
</div>
{showForm && (
<CreateDogForm
       onClose={handleCloseForm}
       onSubmit={handleSubmitForm}
       temperaments={temperaments}
     />
)}
{formSubmitted && (
<div className={styles.successMessage}>
Formulario enviado correctamente.
</div>
)}
<DogList dogs={dogs} onDogClick={handleDogClick} />
<Pagination />
<p>¡Listo! Este es el mensaje de fin de página.</p>
</div>
);
};

export default HomePage;
