import React, { useEffect } from "react";
import styles from "../Styles/Detail.module.css";
import { getDogId } from "../redux/actions/actions";
import { useDispatch, useSelector } from "react-redux";

const Detail = (props) => {
  const { id } = props.match.params; // Acceder al ID de la URL
  const dispatch = useDispatch();
  const DogById = useSelector((state) => state.DogById);
  const dogImage = useSelector((state) => {
    const dog = state.dogs.find((dog) => dog.id === id);
    return dog ? dog.image : null;
  });
  useEffect(() => {
    console.log("ID:", id); 
    dispatch(getDogId(id));
  }, [dispatch, id]);

  return (
    <div className={styles.dogCardContainer}>
      <div className={styles.dogCard} key={DogById.id}>
        <h2>{DogById.name}</h2>
        <p>ID: {DogById.id}</p>
        <p>Height: {DogById.height.metric} cm</p>
        <p>Weight: {DogById.weight.metric} kg</p>
        <p>Life Span: {DogById.life_span}</p>
        <p>Temperament: {DogById.temperament}</p>
        <img src={dogImage} alt={DogById.name} />
      </div>
    </div>
  );
};

export default Detail;
