import React, { useEffect } from "react";
import styles from "../Styles/Detail.module.css";
import { getDogId } from "../redux/actions/actions";
import { useDispatch, useSelector } from "react-redux";

const Detail = ({ id }) => {
  const dispatch = useDispatch();
  const dog = useSelector((state) => state.dog); // Cambiado a 'dog' en lugar de 'dogs'

  useEffect(() => {
    dispatch(getDogId(id));
  }, [dispatch, id]);

  return (
    <div className={styles.dogCardContainer}>
      <div className={styles.dogCard} key={dog.id}>
        <h2>{dog.name}</h2>
        <p>ID: {dog.id}</p>
        <p>Height: {dog.height} cm</p>
        <p>Weight: {dog.weight} kg</p>
        <p>Life Span: {dog.lifeOfYear}</p>
        <p>Temperament: {dog.temperament}</p>
        <img src={dog.image} alt={dog.name} />
      </div>
    </div>
  );
};

export default Detail;
