import React, { useState, useEffect } from "react";
import SearchBar from "./SearchBar";
import DogList from "./DogList";
import FilterOptions from "./FilterOptions";
import SortOptions from "./SortOptions";
import Pagination from "./Pagination";
import CreateDogForm from "./CreateDogForm";
import styles from "../Styles/HomePage.module.css";
import { getTemperaments } from "../redux/actions/actions";

const HomePage = () => {
  const [showForm, setShowForm] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [temperaments, setTemperaments] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    const fetchTemperaments = async () => {
      const fetchedTemperaments = await getTemperaments();
      setTemperaments(fetchedTemperaments);
    };
    fetchTemperaments();
  }, []);

  const handleShowForm = () => {
    setShowForm((prevShowForm) => !prevShowForm);
  };

  const handleCloseForm = () => {
    setShowForm(false);
  };

  useEffect(() => {
    if (showForm) {
      setFormSubmitted(false);
    }
  }, [showForm]);

  const handleSubmitForm = () => {
    setFormSubmitted(true);
  };

  const handleFilter = async (data) => {
    setFilteredData(data);
  };

  const handleSort = async (data) => {
    setFilteredData(data);
  };

  return (
    <div>
      <h1 className={styles.title}>Razas de Perros</h1>
      <div className={styles.buttonContainer}>
        <button className={styles.button} onClick={handleShowForm}>
          Agregar nueva raza
        </button>
        <SearchBar />
        <FilterOptions
          temperaments={temperaments}
          onFilter={handleFilter}
          onSort={handleSort}
        />
        <SortOptions />
      </div>
      {showForm && (
        <CreateDogForm
          onClose={handleCloseForm}
          onSubmit={handleSubmitForm}
        />
      )}
      {formSubmitted && (
        <div className={styles.successMessage}>
          Formulario enviado correctamente.
        </div>
      )}
      <DogList filteredData={filteredData} />
      <Pagination />
    </div>
  );
};

export default HomePage;
