        <h2>{dogs.name}</h2>
        <p>ID: {dogs.id}</p>
        <p>Height: {dogs.height} cm</p>
        <p>Weight: {dogs.weight} kg</p>
        <p>Life Span: {dogs.lifeOfYear}</p>
        <p>Temperament: {dogs.temperament}</p>
        <img src={dogs.image} alt={dogs.name} />
      </div>
    </div>
  );
};

export default Detail;
