import { useState, useEffect } from "react";

const CreateDogForm = () => {
  const [name, setName] = useState("");
  const [image, setImage] = useState("");
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [temperaments, setTemperaments] = useState([]);
  const [allTemperaments, setAllTemperaments] = useState([]);

  useEffect(() => {
    const fetchTemperaments = async () => {
      const response = await fetch("/temperaments");
      const data = await response.json();
      setAllTemperaments(data);
    };
    fetchTemperaments();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newDog = { name, image, height, weight, temperaments };

    try {
      const response = await fetch("/dogs", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newDog),
      });
      const data = await response.json();
      console.log(data); // do something with the response from the server
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="image">Image URL:</label>
        <input
          type="text"
          id="image"
          value={image}
          onChange={(e) => setImage(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="height">Height:</label>
        <input
          type="number"
          id="height"
          min="0"
          max="200"
          value={height}
          onChange={(e) => setHeight(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="weight">Weight:</label>
        <input
          type="number"
          id="weight"
          min="0"
          max="100"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="temperaments">Temperaments:</label>
        <select
          id="temperaments"
          multiple
          value={temperaments}
          onChange={(e) =>
            setTemperaments(
              Array.from(e.target.selectedOptions, (option) => option.value)
            )
          }
        >
          {allTemperaments.map((temp) => (
            <option key={temp.id} value={temp.name}>
              {temp.name}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Create Dog</button>
    </form>
  );
};

export default CreateDogForm;
