import { GET_DOGS, GET_TEMPERAMENTS, GET_DOG_ID,POST_DOGS, FILTER_TEMPERAMENTS, FILTER_BY_BREEDS,SORT_BREEDS} from './actions/types';

const initialState = {
  dogs: [],
  currentDog: null,
  temperaments: [],
  DogById: {},
  filter:[],
  createdDog:[],
  DogsByBreed:[],
  filters: {
    name: '',
    temperament: '',
    weight: '',
    height: '',
    life_span: '',
  },
  sort: '',
  page: 1,
};

const dogReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_DOGS:
            return {
                 ...state,
                 dogs: action.payload
             }
    case GET_TEMPERAMENTS:
            return {
              ...state,
              temperaments: action.payload
            };
    case GET_DOG_ID:
            return {
              ...state,
              DogById: action.payload
            };
    case POST_DOGS:
            return {
              ...state,
              createdDog: action.payload
            };
    case FILTER_BY_BREEDS:
      return{
        ...state,
        DogsByBreed: action.payloads
      }


    case FILTER_TEMPERAMENTS: {
      const dogs= state.dogs
      const filterByTemp = action.payload === "All"
        ? []
        : dogs.filter((dog) =>{
          if(dog.temperament){
            return(dog.temperament.includes(action.payload))


          }
          else{return false}
        })
      return {
        ...state,

        filter: filterByTemp,
      };
    }
    case SORT_BREEDS: {
      const { sortOption } = action.payload;

    let sortedBreeds = [...state.breeds]; // Copia las razas de perros actuales

    switch (sortOption) {
      case 'name-asc':
        sortedBreeds.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case 'name-desc':
        sortedBreeds.sort((a, b) => b.name.localeCompare(a.name));
        break;
      case 'weight-asc':
        sortedBreeds.sort((a, b) => {
          const aAverageWeight = (a.weight[0] + a.weight[1]) / 2;
          const bAverageWeight = (b.weight[0] + b.weight[1]) / 2;
          return aAverageWeight - bAverageWeight;
        });
        break;
      case 'weight-desc':
        sortedBreeds.sort((a, b) => {
          const aAverageWeight = (a.weight[0] + a.weight[1]) / 2;
          const bAverageWeight = (b.weight[0] + b.weight[1]) / 2;
          return bAverageWeight - aAverageWeight;
        });
        break;
      default:
        break;
    }

    return {
      ...state,
      breeds: sortedBreeds,
      sortOption,
    };
  }

  case SET_FILTER: {
    const { filter } = action.payload;

    let filteredBreeds = [...state.breeds]; // Copia las razas de perros actuales

    // Aplica el filtro a las razas de perros
    filteredBreeds = filteredBreeds.filter(breed => {
      // Aplica la lógica de filtrado según tus criterios
      // Ejemplo: filtrar por nombre que contiene el filtro
      return breed.name.toLowerCase().includes(filter.toLowerCase());
    });

    return {
      ...state,
      breeds: filteredBreeds,
      filter,
    };

    }

    default:
      return state;
  }
};

export default dogReducer;
