import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import { getDogs } from "../redux/actions/actions";

const DogList = () => {
  const dogs = useSelector(state => state.dogs);
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchDogs = async () => {
      let url = "http://localhost:3001/dogs";
      if (searchTerm) {
        url += `name=${searchTerm}&`;
      }
      if (filter) {
        url += `temperament=${filter}&`;
      }
      if (sort) {
        url += `sort=${sort}&`;
      }
      try {
        const response = await axios.get(url);
        setDogs(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchDogs();
  }, [searchTerm, filter, sort]);

  return (
    <div>
      {dogs.map((dog) => (
        <div key={dog.id}>
          <h2>{dog.name}</h2>
          <p>Height: {dog.height} cm</p>
          <p>Weight: {dog.weight} kg</p>
          <p>Life Span: {dog.life_span} years</p>
          <p>Breed Group: {dog.breed_group}</p>
          <p>Temperament: {dog.temperament}</p>
          <img src={dog.image_url} alt={dog.name} />
        </div>
      ))}
    </div>
  );
};

export default DogList;
