import React, { useState, useEffect } from "react";
import SearchBar from "./SearchBar";
import DogList from "./DogList";
import FilterOptions from "./FilterOptions";
import Pagination from "./Pagination";
import CreateDogForm from "./CreateDogForm";
import styles from  "../Styles/HomePage.module.css";
import axios from "axios";
import { getTemperaments } from "../redux/actions/actions";

const HomePage = () => {
  const [showForm, setShowForm] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [temperaments, setTemperaments] = useState([]);
  const [dogs, setDogs] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filter, setFilter] = useState("");
  const [sort, setSort] = useState("");

  useEffect(() => {
    const fetchTemperaments = async () => {
      try {
        const response = await axios.get("/temperament");
        getTemperaments(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchTemperaments();
  }, []);

  useEffect(() => {
    const fetchDogs = async () => {
      let url = "/dogs?";
      if (searchTerm) {
        url += `name=${searchTerm}&`;
      }
      if (filter) {
        url += `temperament=${filter}&`;
      }
      if (sort) {
        url += `sort=${sort}&`;
      }
      try {
        const response = await axios.get(url);
        setDogs(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchDogs();
  }, [searchTerm, filter, sort]);

  const handleShowForm = () => {
    setShowForm(!showForm);
  };

  const handleCloseForm = () => {
    setShowForm(false);
  };

  useEffect(() => {
    if (showForm) {
      setFormSubmitted(false);
    }
  }, [showForm]);

  const handleSubmitForm = () => {
    setFormSubmitted(true);
  };

  const handleSearch = (searchTerm) => {
    setSearchTerm(searchTerm);
  };

  const handleFilter = (filter) => {
    setFilter(filter);
  };

  const handleSort = (sort) => {
    setSort(sort);
  };

  return (
    <div>
      <h1 className={styles.title}>Razas de Perros</h1>
      <div className={styles.buttonContainer}>
        <button className={styles.button} onClick={handleShowForm}>
          Agregar nueva raza
        </button>
        <SearchBar onSearch={handleSearch} />
        <FilterOptions
          temperaments={temperaments}
          onFilter={handleFilter}
          onSort={handleSort}
        />
      </div>
      {showForm && (
        <CreateDogForm
          onClose={handleCloseForm}
          onSubmit={handleSubmitForm}
        />
      )}
      {formSubmitted && (
        <div className={styles.successMessage}>Formulario enviado correctamente.</div>
      )}
      <DogList dogs={dogs} />
      <Pagination />
      <p>Este es el mensaje de fin de p√°gina</p>
    </div>
  );
};

export default HomePage;
