import { useState, useEffect } from "react";

const SortOptions = ({ handleFilterByTemperament, handleSortByWeight }) => {
  const [temperaments, setTemperaments] = useState([]);

  useEffect(() => {
    const fetchTemperaments = async () => {
      try {
        const response = await fetch("/temperaments");
        const data = await response.json();
        setTemperaments(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTemperaments();
  }, []);

  return (
    <div>
      <div>
        <button onClick={() => handleSortByWeight("asc")}>Sort by Weight (asc)</button>
        <button onClick={() => handleSortByWeight("desc")}>Sort by Weight (desc)</button>
      </div>
      <div>
        {temperaments.map((temperament) => (
          <button key={temperament.id} onClick={() => handleFilterByTemperament(temperament.name)}>
            {temperament.name}
          </button>
        ))}
      </div>
    </div>
  );
};

export default SortOptions;
