import { useState, useEffect } from "react";
import DogCard from "./DogCard";

const DogList = () => {
  const [dogs, setDogs] = useState([]);
  const [filterByTemperament, setFilterByTemperament] = useState("");
  const [sortByWeight, setSortByWeight] = useState("");
  const [userDogs, setUserDogs] = useState([]);

  useEffect(() => {
    const fetchDogs = async () => {
      let url = "/dogs";
      if (filterByTemperament) {
        url += `?temperament=${filterByTemperament}`;
      }
      if (sortByWeight === "asc") {
        url += url.includes("?") ? "&" : "?";
        url += "sort=weight";
      } else if (sortByWeight === "desc") {
        url += url.includes("?") ? "&" : "?";
        url += "sort=-weight";
      }

      try {
        const response = await fetch(url);
        const data = await response.json();
        setDogs(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchDogs();
  }, [filterByTemperament, sortByWeight]);

  const handleFilterByTemperament = (temperament) => {
    setFilterByTemperament(temperament);
  };

  const handleSortByWeight = (order) => {
    setSortByWeight(order);
  };

  return (
    <div>
      <div>
        <button onClick={() => handleSortByWeight("asc")}>Sort by Weight (asc)</button>
        <button onClick={() => handleSortByWeight("desc")}>Sort by Weight (desc)</button>
      </div>
      <div>
        <button onClick={() => handleFilterByTemperament("docile")}>Docile</button>
        <button onClick={() => handleFilterByTemperament("friendly")}>Friendly</button>
        <button onClick={() => handleFilterByTemperament("energetic")}>Energetic</button>
      </div>
      {dogs.map((dog) => (
        <DogCard
          key={dog.id}
          id={dog.id}
          name={dog.name}
          image={dog.image}
          temperaments={dog.temperaments}
          weight={dog.weight}
        />
      ))}
    </div>
  );
};

export default DogList;
