import React, { useEffect } from "react";
import { Provider, useDispatch, useSelector } from "react-redux";
import { getDogId } from "../redux/actions/actions";

const Detail = ({ match }) => {
  const dogId = match.params.id;
  const dog = useSelector((state) => state.dogs.find((dog) => dog.id === dogId));
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getDogId(dogId));
  }, [dispatch, dogId]);

  return (
    <Provider><div>
      {dog ? (
        <div>
          <h2>{dog.name}</h2>
          <p>Height: {dog.height} cm</p>
          <p>Weight: {dog.weight} kg</p>
          <p>Life Span: {dog.life_span} years</p>
          <p>Breed Group: {dog.breed_group}</p>
          <p>Temperament: {dog.temperament}</p>
          <img src={dog.image_url} alt={dog.name} />
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div></Provider>
  );
};

export default Detail;
