import { ADD_NEW_DOG, GET_DOGS, SET_CURRENT_DOG, CLEAR_CURRENT_DOG, GET_TEMPERAMENTS, SET_FILTERS, SET_SORT, SET_PAGE } from './actions/types';

const initialState = {
  dogs: [],
  currentDog: null,
  temperaments: [],
  filters: {
    name: '',
    temperament: '',
    weight: '',
    height: '',
    life_span: '',
  },
  sort: '',
  page: 1,
};

const dogReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_NEW_DOG:
      return {
        ...state,
        dogs: [...state.dogs, action.payload],
      };
    case GET_DOGS:
      return {
        ...state,
        dogs: action.payload,
      };
    case SET_CURRENT_DOG:
      return {
        ...state,
        currentDog: action.payload,
      };
    case CLEAR_CURRENT_DOG:
      return {
        ...state,
        currentDog: null,
      };
    case GET_TEMPERAMENTS:
      return {
        ...state,
        temperaments: action.payload,
      };
    case SET_FILTERS:
      return {
        ...state,
        filters: {
          ...state.filters,
          ...action.payload,
        },
      };
    case SET_SORT:
      return {
        ...state,
        sort: action.payload,
      };
    case SET_PAGE:
      return {
        ...state,
        page: action.payload,
      };
    default:
      return state;
  }
};

export default dogReducer;
