import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getDogs, getDogsByBreed } from '../redux/actions/actions';
import Styles from "../Styles/SearchBar.module.css"
const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);
  const [dropdownOptions, setDropdownOptions] = useState([]);
  const dispatch = useDispatch();
  const dogs = useSelector((state) => state.dogs);

  const handleInputChange = (event) => {
    const value = event.target.value;
    setSearchTerm(value);

    // Filtrar las opciones coincidentes
    const filteredOptions = dogs.filter(dog) 
    setDropdownOptions(filteredOptions);

    // Mostrar u ocultar el menú desplegable
    setShowDropdown(value.length > 0 && filteredOptions.length > 0);
  };

  const handleSearch = (event) => {
    event.preventDefault();
    dispatch(getDogsByBreed(searchTerm));
    setSearchTerm('');
    setShowDropdown(false);
  };

  const handleDropdownOptionClick = (breed) => {
    dispatch(getDogsByBreed(breed));
    setSearchTerm('');
    setShowDropdown(false);
  };

  return (
    <div className="search-bar">
      <input
        type="text"
        value={searchTerm}
        onChange={handleInputChange}
        placeholder="Buscar raza de perro"
      />
      <button onClick={handleSearch}>Buscar</button>
      {showDropdown && (
        <ul className="dropdown">
          {dropdownOptions.map((dog) => (
            <li
              key={dog.id}
              onClick={() => handleDropdownOptionClick(dog.breed)}
            >
              {dog.breed}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchBar;
