import { useState, useEffect } from "react";

const CreateDogForm = () => {
  const [name, setName] = useState("");
  const [image, setImage] = useState("");
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [temperaments, setTemperaments] = useState([]);
  const [allTemperaments, setAllTemperaments] = useState([]);

  useEffect(() => {
    const fetchTemperaments = async () => {
      const response = await fetch("/temperaments");
      const data = await response.json();
      setAllTemperaments(data);
    };
    fetchTemperaments();
  }, []);

  const handleTemperamentChange = (e) => {
    const selectedTemperaments = Array.from(
      e.target.selectedOptions,
      (option) => option.value
    );
    setTemperaments(selectedTemperaments);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newDog = { name, image, height, weight, temperaments };

    try {
      const response = await fetch("/dogs", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newDog),
      });
      const data = await response.json();
      console.log(data); // do something with the response from the server
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="image">Image URL:</label>
        <input
          type="text"
          id="image"
          value={image}
          onChange={(e) => setImage(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="height">Height:</label>
        <input
          type="text"
          id="height"
          value={height}
          onChange={(e) => setHeight(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="weight">Weight:</label>
        <input
          type="text"
          id="weight"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="temperaments">Temperaments:</label>
        <select
          id="temperaments"
          value={temperaments}
          onChange={handleTemperamentChange}
          multiple
        >
          {allTemperaments.map((temperament) => (
            <option key={temperament.id} value={temperament.name}>
              {temperament.name}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Create Dog</button>
    </form>
  );
};

export default CreateDogForm;
