import { useState } from "react";

export default function FilterOptions({ temperaments, onFilter, onSort }) {
  const [selectedTemperament, setSelectedTemperament] = useState("");
  const [selectedSortOption, setSelectedSortOption] = useState("");

  const handleFilter = (event) => {
    setSelectedTemperament(event.target.value);
    onFilter(event.target.value);
  };

  const handleSort = (event) => {
    setSelectedSortOption(event.target.value);
    onSort(event.target.value);
  };

  return (
    <div>
      <label htmlFor="temperament-filter">Filter by Temperament:</label>
      <select
        id="temperament-filter"
        name="temperament-filter"
        value={selectedTemperament}
        onChange={handleFilter}
      >
        <option value="">All</option>
        {temperaments.map((temperament) => (
          <option key={temperament} value={temperament}>
            {temperament}
          </option>
        ))}
      </select>

      <label htmlFor="sort-by">Sort by:</label>
      <select
        id="sort-by"
        name="sort-by"
        value={selectedSortOption}
        onChange={handleSort}
      >
        <option value="">Default</option>
        <option value="name-asc">Name (A-Z)</option>
        <option value="name-desc">Name (Z-A)</option>
      </select>
    </div>
  );
}
