import axios from 'axios';
import {
  GET_DOGS,
  GET_TEMPERAMENTS,
  GET_DOG_ID,
  POST_DOGS

} from "./types";

// Action creator para obtener la lista de perros
export async function getDogs(){
         const {data} = await axios("https://api.thedogapi.com/v1/breeds");
         return { type: GET_DOGS, payload: data};
     }

export async function getDogId(id) {
      const { data } = await axios(`https://api.thedogapi.com/v1/breeds/${id}`);
      return { type: GET_DOG_ID, payload: data };
    }

export async function getTemperaments() {
      const { data } = await axios("https://api.thedogapi.com/v1/breeds");
      return { type: GET_TEMPERAMENTS, payload: data };
    }

export async function createNewDog(newDog) {
      const { data } = await axios.post("https://api.thedogapi.com/v1/breeds", newDog);
      return { type: POST_DOGS, payload: data };
    }
export const searchDogsByName = (name) => {
      return (dispatch) => {
        return axios.get(`/dogs?name=${name}`)
          .then(response => {
            dispatch({ type: GET_DOGS_BY_NAME, payload: response.data });
          })
          .catch(error => {
            throw(error);
          });
      };
    };
