import { GET_DOG_ID, GET_DOGS, GET_TEMPERAMENTS } from './actions/types.js'
import { POST_DOGS } from './actions/types.js'

const initialState = {
  dogs: [], // Inicializar como un array vacío
  // Otros estados iniciales...
};

const reducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case GET_DOGS:

        return {
          ...state,
          dogs: payload, // Actualizar el estado con los perros recibidos
        };
        case GET_TEMPERAMENTS:

            return {
                ...state
            }
        case POST_DOGS:

            return {
                ...state
            }
        case GET_DOG_ID:

            return {
                ...state
            }

        default:
            return { ...state }
    }
}

export default reducer;
