import React, { useEffect } from "react";
import styles from "../Styles/Detail.module.css";
import { getDogId } from "../redux/actions/actions";
import { useDispatch, useSelector } from "react-redux";

const Detail = (props) => {
  const { id } = props.match.params; // Acceder al ID de la URL
  const dispatch = useDispatch();
  const dog = useSelector((state) => state.dogs.find((dog) => dog.id === id));

  useEffect(() => {
    dispatch(getDogId(id));
  }, [dispatch, id]);

  if (!dog) {
    return <div>Cargando...</div>;
  }

  const { name, height, weight, lifeOfYear, temperament, image } = dog;

  return (
    <div className={styles.dogCardContainer}>
      <div className={styles.dogCard}>
        <h2>{name}</h2>
        <p>ID: {id}</p>
        <p>Height: {height} cm</p>
        <p>Weight: {weight} kg</p>
        <p>Life Span: {lifeOfYear}</p>
        <p>Temperament: {temperament}</p>
        <img src={image} alt={name} />
      </div>
    </div>
  );
};

export default Detail;
