import React from "react";
import styles from "../Styles/Detail.module.css"
import { getDogs, getTemperaments } from "../redux/actions/actions";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { getDogId } from "../redux/actions/actions";
const Detail = () => {

  const { id } = useParams();
  const [dogs, setCharacter] = useState({});

  useEffect(() => {
    async function inEffect() {
      try {
        const { data } = await axios(
          `http://localhost:3001/dogs/${id}`
        );
        if (data.name) {
          setCharacter(data);
        } else {
          window.alert("No hay personajes con ese ID");
        }
      } catch (error) {
        console.log("character update in useeffect in component Detail", error);
      }
    }
    inEffect();
    return setCharacter({}); // cuando se desmonta -> {}
  }, [id]);
    return (
    <div className={styles.dogCardContainer}>
      {dogs?.map((dog) => (
      <div className={styles.dogCard} key={dog.id}>
        <h2>{dog.name}</h2>
        <p>ID: {dog.id}</p>
        <p>Height: {dog.height} cm</p>
        <p>Weight: {dog.weight} kg</p>
        <p>Life Span: {dog.lifeOfYear} </p>
        <p>Temperament: {dog.temperament}</p>
        <img src={dog.image} alt={name} />
      </div>
      ))};
    </div>
  );
}

export default Detail;
