import { useState } from "react";

export default function FilterOptions({ onFilter, onSort }) {
  const [selectedTemperament, setSelectedTemperament] = useState("");
  const [selectedSortOption, setSelectedSortOption] = useState("");

  const handleFilter = async (event) => {
    setSelectedTemperament(event.target.value);
    const res = await fetch(`/dogs?temperament=${event.target.value}`);
    const data = await res.json();
    onFilter(data); // Usa la funci贸n onFilter pasada como prop
  };

  const handleSort = async (event) => {
    setSelectedSortOption(event.target.value);
    const res = await fetch(`/dogs?sort=${event.target.value}`);
    const data = await res.json();
    onSort(data); // Usa la funci贸n onSort pasada como prop
  };

  return (
    <div>
      <label htmlFor="temperament-filter">Filter by Temperament:</label>
      <select
        id="temperament-filter"
        name="temperament-filter"
        value={selectedTemperament}
        onChange={handleFilter}
      >
        <option value="">All</option>
        {temperaments.map((temperament) => (
          <option key={temperament} value={temperament}>
            {temperament}
          </option>
        ))}
      </select>

      <label htmlFor="sort-by">Sort by:</label>
      <select
        id="sort-by"
        name="sort-by"
        value={selectedSortOption}
        onChange={handleSort}
      >
        <option value="">Default</option>
        <option value="name-asc">Name (A-Z)</option>
        <option value="name-desc">Name (Z-A)</option>
        <option value="weight-asc">Weight (Lowest to Highest)</option>
        <option value="weight-desc">Weight (Highest to Lowest)</option>
      </select>
    </div>
  );
}
